INSERT INTO neos.ddfa_main_domain_model_marketentry (persistence_object_identifier, parent_entry_id, category_id, offer, datefrom, dateto, timefrom, timeto, area, name, description, mail, web, facebook, phone, subcategory, type, speakerpublic, speakerprivate, image, imagetype, supportwanted, forchildren, spokenlanguages, published, internalcomment, entry_id, locale, created, updated) VALUES ('bd537a68-9622-49c9-9d60-65f7df07822a', null, null, 1, null, null, null, null, 'dresden', '', 'This is an open forum to promote kindness and understanding towards asylum seekers. "Haselbachtal" is a municipality near the cities Dresden and Bautzen in Germany with around 4000 inhabitants. By March 1, 35 refugees are going to move there. The "Haselbachtalerians" do have a lot of concerns and worries about the newcomers - and a lot of people started to support a Facebook group called "Haselbachtal wehrt sich" (Engl. "Haselbachtal defends itself") claiming that they are confronted with a flood of refugees and ignorant despotism. (Btw: Only few foreigners live in this region compared to others in Germany.) The members of our group, however, do not believe that we are in a state of some kind of war and want to support the idea of a welcoming society showing solidarity - with their hearts and minds. This is what the group name stands for: With our hearts (German: Herzen) and minds (German: Verstand) we want to engage in a fair debate providing information on migration and the asylum system as well as by sharing experiences in order to reduce prejudice and fear. Everybody is welcome here! In this group, and in the society that we want to create. We start NOW - in my hometown.', '', '', '', '', '', 0, '', '', null, null, 0, 0, '', 1, '', '55c464fa1b908', 'en', '2015-11-29 20:17:28', '2015-11-29 20:18:14');
