UPDATE `ddfa_main_domain_model_marketentry` SET `persistence_object_identifier` = '75dba53d-84da-40fa-99c6-6d7d42cf4e1d',`parent_entry_id` = '29bba3a6-35d5-4164-97da-ac9aa515f8b2',`category_id` = '07f88130-7d98-4a20-bbb8-d0d98f55c553',`offer` = 1,`datefrom` = '',`dateto` = '',`timefrom` = '',`timeto` = '',`area` = 'dresden',`name` = 'Ausländerrat Dresden e.V. - Bildungspatenschaften ',`description` = 'Was sind „Bildungspatenschaften“?\r\nIn den Bildungspatenschaften werden ehrenamtlich tätige PatInnen an Kinder und Jugendliche mit Migrationshintergrund vermittelt, um kostenlos deren schulische Entwicklung zu fördern und sie beim Erlernen der deutschen Sprache zu unterstützen. Darüber hinaus sind, neben der schulischen Bildung, auch andere Patenschaftsmodelle (wie beispielsweise die Unterstützung in der Ausbildung) möglich, die je nach Bedarf individuell ausgestaltet werden können.\r\nEine Bildungspatenschaft ist jedoch mehr als nur Hausaufgabenbetreuung: Durch regelmäßige Treffen und Gespräche entsteht ein gegenseitiges Vertrauensverhältnis und PatInnen können als Bezugspersonen und AnsprechpartnerInnen bei Fragen und Problemen im Alltag den Kindern und Jugendlichen zur Seite stehen. Im Rahmen der Patenschaft können Kinder und Jugendliche, abgestimmt auf ihre Bedürfnisse, Interessen und Stärken, von ihren PatInnen individuell begleitet und gefördert werden. Die PatInnen bekommen oft auch Einblicke in das familiäre Umfeld und stehen in Kontakt zu den Eltern. Nicht selten sind sie für diese AnsprechpartnerInnen und Brücke zu anderen Angeboten sowie zu Unterstützungsmöglichkeiten im sozialen Nahraum.',`mail` = 'bildungspatenschaften@auslaenderrat.de',`web` = '',`facebook` = '',`phone` = '0351 40754252',`subcategory` = 'sponsorship',`type` = 0,`speakerpublic` = 'Antje Großmann',`speakerprivate` = '',`image` = '',`imagetype` = '',`supportwanted` = 0,`forchildren` = 1,`spokenlanguages` = '',`published` = 1,`internalcomment` = '',`entry_id` = '55ef3082894ec',`locale` = 'de