UPDATE `ddfa_main_domain_model_marketentry` SET `persistence_object_identifier` = '0d3cad8a-72dc-48f5-b95e-9360554a9abb',`parent_entry_id` = NULL,`category_id` = 'fa815876-ad1d-433e-87ff-8de20639e2b1',`offer` = 1,`datefrom` = '',`dateto` = '',`timefrom` = '',`timeto` = '',`area` = 'dresden',`name` = 'medinetz',`description` = 'Die Menschenrechtsinitiative Medinetz Dresden e.V. vermittelt anonym und kostenlos medizinische Hilfe für Geflüchtete und Menschen ohne Aufenthaltsstatus.\r\n Wir sind eine unabhängige Initiative von Studierenden und engagierten ÄrztInnen, die von vielen Dresdener ÄrztInnen, Pflegenden, PsychotherapeutInnen, Hebammen und DolmetscherInnen unterstützt wird. \r\nUnser primäres Ziel ist es, Menschen ohne Papiere in Dresden und Umland Zugang zu einer medizinischen Versorgung zu ermöglichen, weil diese von staatlicher Seite nicht gewährleistet wird.\r\nDer Anspruch auf eine angemessene medizinische Versorgung ist ein Menschenrecht, welches Menschen ohne Papiere im Moment faktisch vorenthalten wird. Auch Asylsuchenden wird nur eine eingeschränkte \r\nmedizinische Versorgung gewährt. Mit unserer Arbeit möchten wir den Betroffenen den Zugang ermöglichen.',`mail` = '',`web` = 'http://www.semaz.monoceres.uberspace.de/medinetz/',`facebook` = '',`phone` = '0177-1736781',`subcategory` = 'medical-care',`type` = 0,`speakerpublic` = '',`speakerprivate` = '',`image` = 'http://www.gesundheit-ein-menschenrecht.de/sites/default/files/logo_Medinetz_Dresden.jpg',`imagetype` = 'image',`supportwanted` = 0,`forchildren` = 0,`spokenlanguages` = '',`published` = 1,`internalcomment` = '',`entry_id` = '556e1d2d40468',`locale` = 'de',`created` = '2015-06-02 23:16:29',`updated` = '2016-07-19 10:12:35' WHERE `ddfa_main_domain_model_marketentry`.`persistence_object_identifier` = '0d3cad8a-72dc-48f5-b95e-9360554a9abb';
