UPDATE `ddfa_main_domain_model_marketentry` SET `persistence_object_identifier` = '2c8b4eb1-7bbc-41bb-8abb-9ae27081c505',`parent_entry_id` = NULL,`category_id` = '07f88130-7d98-4a20-bbb8-d0d98f55c553',`offer` = 1,`datefrom` = '',`dateto` = '',`timefrom` = '',`timeto` = '',`area` = 'dresden',`name` = 'Montagscafé Staatsschauspiel',`description` = 'Jeden Montag hat ab 15.00 Uhr das Montagscafé im Kleinen Haus des Staatsschauspiel Dresden geöffnet.\nDas Kleine Haus bietet einen offenen Treffpunkt für Geflüchtete und Dresdnerinnen und Dresdner: MitarbeiterInnen des Theaters, Geflüchtete, ZuschauerInnen oder BürgerbühnendarstellerInnen – jede und jeder kann einfach vorbeikommen. Das Montagscafé ist ein Angebot für alle Altersgruppen.\n\nUm 15:00 Uhr geht es los mit\na) einem Theaterworkshop, der von Schauspielern und Theaterpädagogen geleitet wird. Es sind keine Theater- und Sprachkenntnisse erforderlich. Wer Lust hat, kommt einfach spontan vorbei!\nb) einem Café nur für Frauen. Hier wird gemeinsam getanzt, gebastelt, gesungen, Deutsch gelernt oder einfach nur bei einem Tee zusammen gesessen. Immer wieder machen wir auch Ausflüge (z. B. in Museen, zu Workshops o. ä.) Das aktuelle Programm ist auf unserer Internetseite oder bei Facebook einsehbar.\nc) einem Kinderprogramm. Wir basteln, singen und essen gemeinsam - oder toben bei gutem Wetter auf dem Spielplatz hinter dem Kleinen Haus! Jede Woche holen wir dazu ein paar Kinder aus der EAE Hamburger Straße ab - jede/jeder andere ist aber genau so gern gesehen!\n\nUm 17.00 Uhr öffnet dann das Café für Alle. Musik, Snacks, Getränke und Brettspiele laden zu zwanglosem Verweilen und Kennenlernen ein. An einem Infotisch gibt es Informationen zu Dresden und Angeboten anderer Einrichtungen. Hier kann man sich auch für die von uns organisierten Deutschkurse anmelden.\n\nUm 19.00 Uhr finden wöchentlich wechselnde Aktionen wie Vorträge, Tauschbörsen, Lesungen, Konzerte oder Tanzstunden statt. Ideen, Vorschläge, Anregungen und Angebote sind herzlich willkommen.\n\nDer Eintritt zu allen Aktionen des Montagscafés ist frei.',`mail` = 'montagscafe@staatsschauspiel-dresden.de',`web` = 'http://www.staatsschauspiel-dresden.de/home/montagscafe/',`facebook` = 'https://www.facebook.com/montagscafedresden/',`phone` = '0351 - 4913 617',`subcategory` = 'meeting-place',`type` = 0,`speakerpublic` = 'David Lenard',`speakerprivate` = '',`image` = '',`imagetype` = '',`supportwanted` = 0,`forchildren` = 0,`spokenlanguages` = '',`published` = 1,`internalcomment` = NULL,`entry_id` = '55fb28c78f8de',`locale` = 'de',`created` = '2015-09-17 22:55:35',`updated` = '2016-06-26 13:10:10' WHERE `ddfa_main_domain_model_marketentry`.`persistence_object_identifier` = '2c8b4eb1-7bbc-41bb-8abb-9ae27081c505';
